import { useState } from "react"
import { FaChevronRight, FaChevronLeft } from "react-icons/fa"
import "./slaider.css"

const PAGE_WIDTH = 1200

export const Slaider = () => {
  const photos = []
  for (let i = 1; i <= 9; i++) {
    photos.push(`photo_${i}`)
  }

  const [offset, setOffset] = useState(0)

  const handleLeftArrowClick = () => {
    setOffset(currentOffset => {
      const newOffset = currentOffset + PAGE_WIDTH
    })
  }

  function bgImage(imageName) {
    const imageUrl = require(`./../../../public/images/photo_auto/${imageName}.jpg`)
    return `url(${imageUrl})`
  }

  return (
    <section className="main-container">
      <FaChevronLeft className="arrow" />
      <div className="window-container">
        <div className="all-photo-container"
        style={{
          transform: `translateX(${offset}px)`
        }}
        >
          {photos.map((photo_num, number) => {
            return (
              <div
                className={`${photo_num} photo`}
                key={number}
                style={{
                  minWidth: `${PAGE_WIDTH}px`,
                  maxWidth: `${PAGE_WIDTH}px`,
                  backgroundImage: bgImage(`photo${number + 1}`),
                }}
              ></div>
            )
          })}
        </div>
      </div>
      <FaChevronRight className="arrow" />
    </section>
  )
}
