import { useContext, useEffect, useState } from "react"
import { SliderContext } from "../Slider"

const Slide = ({ id, animation }) => {
  const [isLoading, setIsLoading] = useState(true)
  const { WIDTH_PAGE, HEIGHT_PAGE } = useContext(SliderContext)

  function bgImage(imageName) {
    const imageUrl = require(`./../../../images/photo_auto/photo${imageName}.jpg`)
    return imageUrl
  }

  const currentUrlImg = bgImage(`${id + 1}`)
  const currentSlide = new Image()

  useEffect(() => {
    setIsLoading(true)

    currentSlide.src = currentUrlImg
    currentSlide.onload = () => setIsLoading(false)
  }, [currentUrlImg])

  return (
    <>
      {isLoading ? (
        <p>Загрузка...</p>
      ) : (
        <img
          src={currentUrlImg}
          className={`slide-image ${animation ? "coincidental" : ""} ${
            isLoading ? "isLoading" : ""
          }`}
          style={{
            minWidth: WIDTH_PAGE,
            minHeight: HEIGHT_PAGE,
          }}
        />
      )}
    </>

    // <img
    //   src={bgImage(`${id + 1}`)}
    //   className={`slide-image ${animation ? "coincidental" : ""} ${
    //     isLoading ? "isLoading" : ""
    //   }`}
    //   style={{
    //     minWidth: WIDTH_PAGE,
    //     minHeight: HEIGHT_PAGE,
    //   }}
    // />
  )
}

export default Slide
