import { useState } from "react"
import { FaChevronRight, FaChevronLeft } from "react-icons/fa"
import "./slaider.css"

export const Slaider = () => {
  const photos = []
  for (let i = 1; i <= 9; i++) {
    photos.push(`photo_${i}`)
  }

  const [photo, setNumberPhoto] = useState(photos[0])

  function bgImage(imageName) {
    const imageUrl = require(`./../../../public/images/photo_auto/${imageName}.jpg`)
    return { backgroundImage: `url(${imageUrl})` }
  }

  return (
    <section className="main-container">
       <FaChevronLeft className="arrow" onClick={handleLeftArrowClock} />
      <div className="window-container">
        <div className="all-photo-container">
          {photos.map((photo_num, number) => {
            return (
              <div
                className={`${photo_num} photo`}
                key={number}
                style={bgImage(`photo${number + 1}`)}
              ></div>
            )
          })}
        </div>
      </div>
      <FaChevronRight className="arrow" onClick={handleRightArrowClock} />
    </section>
  )
}
