function rangeDate(start, end) {
    const date = new Date
  const options = {
    weekday: "long",
  }
  const arrDay = []

  for (let i = start; i <= end; i++) {
    arrDay.push(new Intl.DateTimeFormat("ru-Ru", options).format(date))
  }

  return arrDay
}

export default rangeDate
