import { useState } from "react"
import "./slaider.css"

export const Slaider = () => {
  
  const [photo, setNumberPhoto] = useState(photos[0])
  
  const photo_qty = 9
  const photos = []
  for (let i = 1; i <= photo_qty; i++) {
    photos.push(`photo_${i}`)
  }

  function bgImage(imageName) {
    const imageUrl = require(`./../../../public/images/photo_auto/${imageName}.jpg`)
    return { backgroundImage: `url(${imageUrl})` }
  }

  return (
    <section className="main">
      <div className="photos">
        {photos.map((photo_num, number) => {
          return (
            <div
              className={`${photo_num} photo`}
              key={number}
              style={bgImage(`photo${number + 1}`)}
            ></div>
          )
        })}
      </div>
    </section>
  )
}
