import { useContext, useEffect, useState } from "react"
import { SliderContext } from "../Slider"

const Slide = ({ id, animation }) => {
  const [isLoading, setIsLoading] = useState(true)
  const { WIDTH_PAGE, HEIGHT_PAGE } = useContext(SliderContext)

  function bgImage(imageName) {
    const imageUrl = require(`./../../../images/photo_auto/photo${imageName}.jpg`)
    return imageUrl
  }

  const currentUrl = bgImage(`${id + 1}`)

  useEffect(() => {
    setIsLoading(true)
    const currentSlide = new Image()
    currentSlide.src = currentUrl
    currentSlide.className = `slide-image ${animation ? "coincidental" : ""} ${
      isLoading ? "isLoading" : ""
    }`
    currentSlide.style = {
      minWidth: WIDTH_PAGE,
      minHeight: HEIGHT_PAGE,
    }
    currentSlide.onload = () => setIsLoading(false)
  }, [currentUrl])

  return (
    <>{currentSlide}
    </>
    // <img
    //   src={bgImage(`${id + 1}`)}
    //   className={`slide-image ${animation ? "coincidental" : ""} ${
    //     isLoading ? "isLoading" : ""
    //   }`}
    //   style={{
    //     minWidth: WIDTH_PAGE,
    //     minHeight: HEIGHT_PAGE,
    //   }}
    // />
  )
}

export default Slide
