function rangeHours() {
  const date = Intl.DateTimeFormat("en-RU", {
    day: "numeric",
    month: "numeric",
    year: "numeric",
  }).format(new Date())
  let arrHours = []

  for (let i = 1; i <= 84; i++) {
    if (i <= 12) {
      arrHours.push(<div id={date}>{i + 9}</div>)
    }
    if (12 < i && i <= 24) {
      arrHours.push(<div id={date.setDate(date.getDate() + 1)}>{i - 3}</div>)
    }
    if (24 < i && i <= 36) {
      arrHours.push(<div id={date.setDate(date.getDate() + 2)}>{i - 15}</div>)
    }
    if (36 < i && i <= 48) {
      arrHours.push(<div id={date.setDate(date.getDate() + 3)}>{i - 27}</div>)
    }
    if (48 < i && i <= 60) {
      arrHours.push(<div id={date.setDate(date.getDate() + 4)}>{i - 39} </div>)
    }
    if (60 < i && i <= 72) {
      arrHours.push(<div id={date.setDate(date.getDate() + 5)}>{i - 51} </div>)
    }
    if (72 < i && i <= 84) {
      arrHours.push(<div id={date.setDate(date.getDate() + 6)}>{i - 63} </div>)
    }
  }
  return arrHours
}

export default rangeHours
