function dateTimeFormat(date, numberDayOfWeek) {
  if (numberDayOfWeek === 1) {
    const dateFormatter = Intl.DateTimeFormat("en-RU", {
        month: "numeric",
      day: "numeric",
      year: "numeric",
    }).format(date)

    return dateFormatter
  }

  if (numberDayOfWeek > 1) {
    const dateFormatter = Intl.DateTimeFormat("en-RU", {
      day: "numeric",
      month: "numeric",
      year: "numeric",
    }).format(date.setDate(date.getDate()+1))

    return dateFormatter
  }
}

export default dateTimeFormat
