import { Link, useLocation } from "react-router-dom"
import style from "./header.module.css"
import MLC from "./android-chrome-512x512.png"

const Header = () => {


  
  const handleVisible = (visibility) => {
    const hiddenElement = document.getElementsByClassName(`${style.nodes}`)

    if ((visibility = "visible")) {
      // hiddenElement[0].classList.remove(`${style.hidden}`)
      hiddenElement[0].classList.add(`${style.visible}`)
      console.log(hiddenElement[0].classList)

      // return
    }

    if ((visibility = "hidden")) {
      console.log("условие выполняется???")
      hiddenElement[0].classList.remove(`${style.visible}`)
      hiddenElement[0].classList.add(`${style.hidden}`)

      return
    }
  }

  return (
    <>
      <header className={style.header}>
        <img src={MLC} />
        <div className={style.header_anchor}>
          <Link to=".">Главная</Link>
          <Link to="photo">Фото</Link>
          <div
            className={style.nodes_trigger}
            onMouseOver={() => {
              handleVisible("visible")
            }}
          >
            Узлы и агрегаты ▾
            <ul className={style.nodes}>
              <div className={style.nodes__wrapper}>
                <Link
                  to="engine"
                  className={style.nodes_anchor}
                  onClick={() => {
                    handleVisible('hidden')
                  }}
                >
                  Двигатель
                </Link>
                <Link to="carbody" className={style.nodes_anchor}>
                  Кузов
                </Link>
                <Link to="pendant" className={style.nodes_anchor}>
                  Подвеска
                </Link>
                <Link to="interior" className={style.nodes_anchor}>
                  Салон
                </Link>
              </div>
            </ul>
          </div>
          <div className={style.line} />
          <div className={style.reviews_block}>
            <Link to="reviews">Отзывы</Link>
            <button> Оставить отзыв</button>
          </div>
        </div>
      </header>
    </>
  )
}

export default Header
