import { useState } from "react"
import { Link, useLocation } from "react-router-dom"
import style from "./header.module.css"
import MLC from "./android-chrome-512x512.png"
import ModalWindow from "../ModalWindow/ModalWindow"

const Header = () => {
  const [dropDownMenu, setDropDownMenu] = useState(false)
  const [modalActive, setModalActive] = useState(false)

  if(modalActive) {
    document.body.style.overflowX = 'hidden'
    document.body.style.overflowy = 'auto'
  } else {
    document.body.style.overflow = ''
  }

  console.log(modalActive)

  return (
    <>
      <header className={style.header}>
        <img src={MLC} />
        <div className={style.header_anchor}>
          <Link to=".">Главная</Link>
          <Link to="photo">Фото</Link>
          <div
            className={style.nodes_trigger}
            onMouseEnter={() => {
              setDropDownMenu(true)
            }}
            onMouseLeave={() => {
              setDropDownMenu(false)
            }}
          >
            Узлы и агрегаты ▾
            <ul
              className={`${style.nodes} ${
                dropDownMenu ? style.dropDownMenu : style.hidden
              }`}
            >
              <div className={style.dummy} />
              <div className={style.nodes__wrapper}>
                <Link
                  to="engine"
                  className={style.nodes_anchor}
                  onClick={() => {
                    setDropDownMenu(false)
                  }}
                >
                  Двигатель
                </Link>
                <Link
                  to="carbody"
                  className={style.nodes_anchor}
                  onClick={() => {
                    setDropDownMenu(false)
                  }}
                >
                  Кузов
                </Link>
                <Link
                  to="pendant"
                  className={style.nodes_anchor}
                  onClick={() => {
                    setDropDownMenu(false)
                  }}
                >
                  Подвеска
                </Link>
                <Link
                  to="interior"
                  className={style.nodes_anchor}
                  onClick={() => {
                    setDropDownMenu(false)
                  }}
                >
                  Салон
                </Link>
              </div>
            </ul>
          </div>
          <div className={style.line} />
          <div className={style.reviews_block}>
            <Link to="reviews">Отзывы</Link>
            <button
              onClick={() => {
                setModalActive(true)
              }}
            >
              {" "}
              Оставить отзыв
            </button>
          </div>
        </div>
      </header>
      <ModalWindow modalActive={modalActive} setModalActive={setModalActive}/>
    </>
  )
}

export default Header
