import { useContext } from "react"
import { SliderContext } from "../../Slider"

const Slide = ({ number, id, animation }) => {
  const { WIDTH_PAGE, HEIGHT_PAGE, slideNumber, bgImage } = useContext(SliderContext)

  

  return (
    
    <div
      className={`photo ${animation ? "coincidental" : ""}`}
      style={{
        // left: `${number * 100}%`,
        minWidth: WIDTH_PAGE,
        height: HEIGHT_PAGE,
        backgroundImage: bgImage(`photo${slideNumber + 1}`),
      }}
    ></div>
  )
}

export default Slide
