import { useContext } from "react"
import { SliderContext } from "../../Slider"

const Slide = ({ number, animation }) => {
  const { WIDTH_PAGE, HEIGHT_PAGE, slideNumber } = useContext(SliderContext)

  function bgImage(imageName) {
    const imageUrl = require(`./../../../../../public/images/photo_auto/${imageName}.jpg`)
    return `url(${imageUrl})`
  }

  return (
    <div
      className={`photo`}
      style={{
        minWidth: WIDTH_PAGE,
        height: HEIGHT_PAGE,
        backgroundImage: bgImage(`photo${slideNumber + 1}`),
      }}
    ></div>
  )
}

export default Slide
